import React, { Component } from 'react';
import axios from 'axios';
export default class User extends Component {
    state = {
 
        // Initially, no file is selected
        selectedFile: null,
        newUser: {
            photo:""
        },
        currentFile: undefined,
      progress: 0,
      message: "",

      fileInfos: [],
      };


     handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('photo', this.state.newUser.photo);
        const forData = new FormData();
    
      // Update the formData object
      forData.append(
        "myFile",
        this.state.selectedFile,
        this.state.selectedFile.name
      );
      let currentFile = this.state.selectedFile[0];

      this.setState({
        progress: 0,
        currentFile: currentFile,
      });
      // Details of the uploaded file
      console.log(this.state.selectedFile);
    
      this.setState({
        progress: Math.round((100 * e.loaded) / e.total),
      });
        axios.post('http://localhost:4000/users/add/', formData)
             .then(res => {
                console.log(res);
                this.setState({
                  message: res.data.message,
                });
             })
             .then((files) => {
              this.setState({
                fileInfos:this.state.selectedFile.name,
              });
            })
             .catch(err => {
                console.log(err);
                this.setState({
                  progress: 0,
                  message: "Could not upload the file!",
                  currentFile: undefined,
                });
             });
             this.setState({
              selectedFiles: undefined,
            });
    }
   

    fileData = () => {
    
        if (this.state.selectedFile) {
           
          return (
            <div>
              <h2>File Details:</h2>
               
  <p>File Name: {this.state.selectedFile.name}</p>
   
               
  <p>File Type: {this.state.selectedFile.type}</p>
   
               
  <p>
                Last Modified:{" "}
                {this.state.selectedFile.lastModifiedDate.toDateString()}
              </p>
   
            </div>
          );
        } else {
          return (
            <div>
              <br />
              <h4>Choose before Pressing the Upload button</h4>
            </div>
          );
        }
      };
     handlePhoto = (e) => {
       //  this.setState({...newUser,photo:e.target.files[0]});
      //  setNewUser({...newUser, photo: e.target.files[0]});
      const newState = {...this.state.newUser, photo: e.target.files[0]}
      this.setState({ newUser: newState})
      this.setState({ selectedFile: e.target.files[0] });
    }
    render(){

  
      return (
        <div>
          {this.state.currentFile && (
          <div className="progress">
            <div
              className="progress-bar progress-bar-info progress-bar-striped"
              role="progressbar"
              aria-valuenow={this.state.progress}
              aria-valuemin="0"
              aria-valuemax="100"
              style={{ width: this.state.progress + "%" }}
            >
              {this.state.progress}%
            </div>
          </div>
        )}

        
        <form onSubmit={this.handleSubmit} encType='multipart/form-data'>
        <label className="btn btn-default">
            <input 
                type="file" 
                accept=".csv"
                name="photo"
                onChange={this.handlePhoto}
            />
      </label>

            <input 
                type="submit"
            />
<div className="alert alert-light" role="alert">
          {this.state.message}
        </div>



        <div className="card">
          <div className="card-header">List of Files</div>
          <ul className="list-group list-group-flush">
            {this.state.fileInfos &&
              this.state.fileInfos.map((file, index) => (
                <li className="list-group-item" key={index}>
                  <a href={file.url}>{file.name}</a>
                </li>
              ))}
          </ul>
        </div>
    
        </form>
        </div>
      )
              }
            }
